AND segment=#{segmentCode}
AND workshop_code=#{workShopCode}
AND workarea_code=#{workAreaCode}
AND source_telecode=#{stationCode}
AND alarm_level IN
#{item}
AND alarm_time BETWEEN #{startTime} AND #{endTime}
group by segment,workshop_code,workarea_code,source_telecode,alarm_level,alarm_type,device_name,alarm_context ) subresult ORDER BY ctime DESC

