mapper.select:
SELECT

FROM alarm_info


        AND segment=#{segment}


        AND alarm_level = #{level}

SELECT

FROM alarm_info


        AND segment=#{segmentCode}


        AND workshop_code=#{workShopCode}


        AND workarea_code=#{workAreaCode}


        AND source_telecode=#{stationCode}


        AND alarm_level=#{level}


        AND alarm_type=#{alarmType}
